import java.util.Scanner;

public class DocDuLieu {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		Scanner scan = new Scanner(System.in);
		
		System.out.println("Vui long nhap du lieu ");
		int x = scan.nextInt();
		System.out.println("Gia tri nhap vao cua x la " + x);
		
		System.out.println("Vui long nhap du lieu của y và z ");
		boolean y = scan.nextBoolean();
		float z = scan.nextFloat();
		System.out.println("Gia trị cua y va z lan luot là " + y +" va " + z);
			
		long l = scan.nextLong();
		double d = scan.nextDouble();
		short sh = scan.nextShort();
		byte b = scan.nextByte();
		String s = scan.nextLine();
		
				
		
		
		
	}

}
